/*
Given a string S, you are allowed to convert it to a palindrome by adding
characters in front of it. Find and return the shortest palindrome you can find
by performing this transformation.

For example:

Given "aacecaaa", return "aaacecaaa".

Given "abcd", return "dcbabcd".
*/

#include <vector>
#include <string>
#include <iostream>
#include <algorithm>

using namespace std;

// trim from start
static inline std::string &ltrim(std::string &s) {
        s.erase(s.begin(), std::find_if(s.begin(), s.end(), std::not1(std::ptr_fun<int, int>(std::isspace))));
        return s;
}

// trim from end
static inline std::string &rtrim(std::string &s) {
        s.erase(std::find_if(s.rbegin(), s.rend(), std::not1(std::ptr_fun<int, int>(std::isspace))).base(), s.end());
        return s;
}

// trim from both ends
static inline std::string &trim(std::string &s) {
        return ltrim(rtrim(s));
}

class Solution {
public:
    string shortestPalindrome(string s) {
        s = trim(s);
        if (s.length() == 0)
            return s;
            
        std::vector<int> dp(s.length(), 0);
        
        int longest_palindrome = 1;
        std::string substring = s.substr(0, s.length());
        std::string reverse = substring;
        std::reverse(reverse.begin(), reverse.end());
        
        for (int i = s.length() - 1; i >= 0; --i) {
            if (substring == reverse) {
                // We figured out where the palindrome should be.
                longest_palindrome = i + 1;
                break;
            }
            
            // Now strip one character from each.
            substring.pop_back();
            reverse.erase(0, 1);
        }
        
        string prefix = s.substr(longest_palindrome, s.length() - longest_palindrome);
        std::reverse(prefix.begin(), prefix.end());
        s = prefix + s;
        return s;
    }
};

int main() {
    Solution soln;
    std::string s = "aacecaaa";
    // s = "";
    s = "ba";
    std::cout << soln.shortestPalindrome(s) << std::endl;
}